INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-07-31 09:39:55', '2013-07-31 09:39:55', 'Benvenuto in WordPress. Questo è il tuo primo articolo. Modificalo o cancellalo e inizia a creare il tuo blog!', 'Ciao mondo!!', '', 'draft', 'open', 'open', '', 'ciao-mondo', '', '', '2013-08-06 11:59:08', '2013-08-06 09:59:08', '', 0, 'http://www.bszimpianti.it/testaruba/?p=1', 0, 'post', '', 0),
(2, 1, '2013-07-31 09:39:55', '2013-07-31 07:39:55', 'Questa è una pagina di esempio. Differisce da un articolo di blog per rimane in un solo posto ed appare nel menu di navigazione del sito (questo nella maggior parte dei temi). Molte persone iniziano con una pagina di Info che li introduce ai potenziali visitatori del sito. Tale pagina potrebbe contenere un testo del tipo:\r\n<blockquote>Salve! Sono un pony express di giorno, un aspirante attore di notte e questo è il mio blog. Vivo a Los Angeles, ho un gran bel cane di nome Jack e mi piace la piña coladas. (E infradiciarmi sotto la pioggia.)</blockquote>\r\n...o cose di questo genere:\r\n<blockquote>La XYZ Doohickey Company è stata fondata nel 1971, e ha fornito doohickeys di qualità al pubblico fin d\'allora. Si trova a Gotham City, XYZ impiega oltre 2,000 persone e produce ogni genere di cose impressionanti per la comunità di Gotham.</blockquote>\r\nDa nuovo utente WordPress puoi andare sulla <a href=\"http://www.bszimpianti.it/testaruba/wp-admin/\">tua bacheca</a> per cancellare questa pagina e creare nuove pagine per i tuoi contenuti. Buon divertimento!', 'Es', '', 'draft', 'closed', 'closed', '', 'es', '', '', '2013-08-06 12:49:21', '2013-08-06 10:49:21', '', 0, 'http://www.bszimpianti.it/testaruba/?page_id=2', 99, 'page', '', 0),
(4, 1, '2013-07-31 12:41:17', '2013-07-31 10:41:17', '<div id=\"imCel1_03\">\r\n<div id=\"imCel1_03_Cont\">\r\n<div id=\"imObj1_03\"><strong style=\"text-align: justify;\">BSZ Impianti Elettrotecnici</strong><span style=\"text-align: justify;\"> è stata costituita nel 1990 e si sviluppa dal consolidamento di una clientela di parte dei soci e dall\'ipotesi di costruzione di una struttura capace di diversificare gli interventi nel settore e di pianificare una capacità di servizio presso il cliente.</span></div>\r\n<div>\r\n<p style=\"text-align: justify;\"><strong><img class=\"wp-image-198 alignleft\" alt=\"azienda2\" src=\"http://www.bszimpianti.it/testaruba/wp-content/uploads/2013/07/p001_1_05-300x225.png\" width=\"216\" height=\"162\" /></strong><strong></strong></p>\r\n<span style=\"text-align: justify;\">La clientela aziendale è costituita da industrie, immobiliari e privati.\r\n</span>Nel marzo 2002 l’azienda ha conseguito la certificazione del Sistema Qualità secondo la norma internazionale UNI EN ISO 9002.\r\n<p style=\"text-align: justify;\">Nel 2004 la <strong>BSZ Impianti Elettrotecnici</strong> approccia un percorso di studio e approfondimento sulle nuove fonti di energia rinnovabile e nel 2005 progetta e realizza sistemi fotovoltaici con il primo realizzato della potenza di 50 kWp.</p>\r\n\r\n</div>\r\n<img class=\"wp-image-197 alignright\" alt=\"azienda1\" src=\"http://www.bszimpianti.it/testaruba/wp-content/uploads/2013/07/p001_1_02-300x225.png\" width=\"216\" height=\"162\" />\r\n\r\n<strong style=\"text-align: justify;\">\r\nS</strong><strong style=\"text-align: justify;\">TRUTTURA ORGANIZZATIVA BSZ IMPIANTI ELETTROTECNICI\r\n</strong><strong>Area Progettazione</strong> con competenza per studio, progettazione, illuminotecnica, energie rinnovabili\r\n2 addetti\r\n<strong>Area Installazione</strong> con competenza per installazione impiantistica\r\n5 addetti\r\n<strong>Area Energia Rinnovabile</strong> con competenza per installazione impiantistica\r\n5 addetti\r\n<strong>Area manutenzione emergenza</strong> con competenza per intervento immediato su guasti\r\n2 addetti\r\n\r\n(Si usufruisce anche di una vasta rete di collaboratori esterni)\r\n\r\n</div>\r\n</div>', 'Azienda', '', 'publish', 'closed', 'closed', '', 'azienda', '', '', '2013-08-06 08:31:01', '2013-08-06 06:31:01', '', 0, 'http://www.bszimpianti.it/testaruba/?page_id=4', 0, 'page', '', 0),
