INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(338, 1, '2012-01-21 11:45:35', '2012-01-21 10:45:35', '<span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Quarto conto energia e stretta del credito provocano mutazioni nella struttura e negli attori del mercato italiano del fotovoltaico. Da qualche mese e per tutto il 2012 sempre più in uso è l\'acquisizione in comodato d\'uso dei tetti dei capannoni, soprattutto agricoli. Del fenomeno si parlerà alla sesta edizione di Microgen Forum.\r\n</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Fare soldi prendendo in comodato le superfici e piazzandoci sopra impianti di generazione fotovoltaica, corrispondendo qualcosa al proprietario e incassando gli ancora ricchi sussidi (con rendimenti dell\'8-10 per cento l\'anno per vent\'anni sul capitale investito, meglio dei BTp). Si tratta di </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>uno dei trend di sviluppo</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> delle rinnovabili nel nostro Paese che è protagonista dell\'annuale </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>”Rapporto sulle nuove energie in Italia”</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, giunto alla terza edizione, che verrà presentato nel corso di Microgen Forum a Milano il prossimo 21 marzo. </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Il fenomeno della </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>caccia ai tetti</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, presente in altri Paesi ma finora marginale da noi, ha iniziato a manifestarsi con forza negli ultimi mesi del 2011 a seguito di due condizioni. Da una parte, l\'entrata in vigore del </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>Quarto Conto Energia</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, che di fatto continua a garantire sussidi generosi solo agli impianti su edifici inferiori al MW di potenza; dall\'altra, il </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>credit </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>crunch</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, che ha reso molto difficile l\'accesso ai finanziamenti in prima persona da parte dei </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">proprietari dei tetti, di solito aziende agricole o singoli agricoltori, anche per progetti di fatto garantiti.</span></span></span>\r\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">La finestra resterà aperta per </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>un tempo limitato</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">: già dal gennaio 2013 i sussidi perderanno la componente acquisto dell\'energia, che oggi rende per circa 10 centesimi a KWh, dando un taglio deciso ai rendimenti. Ed ecco i cacciatori di tetti in giro per la campagne di tutta Italia, con privilegio per le aree con numerosi allevamenti zootecnici, che sono le aziende con </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>i capannoni più grandi</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">. Armati di brochure e macchine fotografiche digitali, con competenze di impianti fotovoltaici ma anche di strutture edilizie e coperture, si muovono per lo più guidati dal passaparola da un agricoltore all\'altro. Sono aiutati anche dagli incentivi (crediti d\'imposta) legati alla rimozione e </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>smaltimento delle </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>coperture contenenti amianto</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, e possono proporre di prendere due piccioni con una </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">fava.\r\n</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>I “mandanti” </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">dei cacciatori d tetti sono dei tipi più diversi. Ci sono le tradizionali utilities,</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">come Sorgenia, appartenente a un gruppo pioniere nel campo dei comodati dei tetti. Ma ci sono anche aziende più specializzate, dei broker che si occupano delle pratiche burocratiche e di mettere insieme proprietari di tetti, impiantisti, smaltitori di rifiuti speciali e soprattutto </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>finanziatori</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">. Sono questi ultimi infatti i veri “dei ex machina” del business dei tetti. Fondi d\'investimento, coordinamenti di facoltosi privati, banche, tutti soggetti con a disposizione grossi capitali, che ragionano in termini di ritorno dell\'investimento.</span></span></span></p>\r\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Per costoro le figure dei broker indipendenti cacciatori di tetti sono indispensabili, anche perchè consentono una </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>flessibilità</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> di dimensionamento degli impianti impossibile in altro modo. Per fare un esempio: diventa possibile </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>aggirare il vincolo del MW</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> su una certa superficie previsto dal Quarto Conto Energia. Un singolo investitore non potrebbe per esempio sfruttare i tetti di un grosso allevamento con una superficie utile di 1,8 MW. Ma con un broker si può, perché vengono realizzati due impianti da 900 KW l\'uno costruendo l\'impianto in unica soluzione, sfruttando le economie di scala, per poi piazzare le due metà a due investitori diversi.</span></span></span></p>\r\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">In tutto questo giro di uova d\'oro, </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>cosa ci guadagna</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> la gallina, ossia </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>i proprietari di tetti</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">? Le soluzioni di renumerazione proposte sono tre: sostituzione gratuita delle coperture (la meno sfruttata); cambio merce; denaro. L\'ultima apparentemente è la più allettante, ma basta fare due conti per scoprire che non lo è poi tanto. Per un impianto da 200 KW (la </span></span></span><span style=\"color: #535353;\">“<span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">taglia” che probabilmente andrà di più nel 2012, stante la struttura dei sussidi) chi prende in comodato il tetto per vent\'anni garantisce in media un canone anticipato in unica soluzione di 50.000 euro complessivi, che fanno 2.500 euro l\'anno, sui quali poi però vanno calcolate le tasse. Considerando che i 200 KW nei vent\'anni di funzionamento garantiranno 1,5 milioni di euro a fronte di un investimento d\'impianto e accessori (sistemi di videosorveglianza a distanza) non superiori a 550.000 euro, al proprietario rimangono le </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>briciole</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">.</span></span></span></p>\r\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Per questo sta prendendo piede l\'alternativa del </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>cambio merce</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, in due forme: </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>energia </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>elettrica </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">(ma dipende qual\'è la bolletta media) oppure</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b> un impianto fotovoltaico gratuito </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">chiavi in mano completo di autorizzazioni e allacciamenti. Con 50.000 euro oggi </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">si fa, ai prezzi che spuntano i grandi impianti, un\'installazione da 20 KW, che produrranno in vent\'anni 156.000 euro, se l\'impianto viene realizzato nel primo semestre 2012.</span></span></span></p>\r\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Come si vede sono cifre considerevoli. La caccia ai tetti da parte di operatori broker specializzati è soprattutto importante nell\'economia generale del fotovoltaico italiano perchè </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>consente agli investitori professionali</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, che sono stati i veri driver dietro il boom del mercato, </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>di rimanere in gioco</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> anche a fronte del forte freno posto dal Governo alle grandi installazioni.</span></span></span></p>\r\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Nessun fondo d\'investimento si sobbarcherebbe le pratiche e la direzione lavori per un impianto da 200 KW su un capannone, ma se tutto quel che deve fare è firmare un assegno e cominciare a incassare il sussidio, la cosa cambia. Per questo il Rapporto sulle nuove energie in Italia prevede che anche </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>il 2012 sarà superiore alle previsioni per </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>quanto riguarda lo sviluppo del fotovoltaico </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">(esattamente come lo aveva previsto,</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">contro il parere di molti esperti, nel 2011). Le proiezioni definitive verranno rilasciate nel corso di Microgen Forum il prossimo 21 marzo a Milano, ma già c\'è il sospetto che </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>a fine </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>anno la meta dei 20 GW installati non sarà lontana</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">.</span></span></span></p>', 'Il fotovoltaico dei cacciatori di tetti', '', 'publish', 'closed', 'open', '', 'il-fotovoltaico-dei-cacciatori-di-tetti', '', '', '2013-08-06 11:47:51', '2013-08-06 09:47:51', '', 0, 'http://www.bszimpianti.it/testaruba/?p=338', 0, 'post', '', 0),
(339, 1, '2013-08-06 11:47:40', '2013-08-06 09:47:40', '<span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Quarto conto energia e stretta del credito provocano mutazioni nella struttura e negli attori del mercato italiano del fotovoltaico. Da qualche mese e per tutto il 2012 sempre più in uso è l\'acquisizione in comodato d\'uso dei tetti dei capannoni, soprattutto agricoli. Del fenomeno si parlerà alla sesta edizione di Microgen Forum.\n</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Fare soldi prendendo in comodato le superfici e piazzandoci sopra impianti di generazione fotovoltaica, corrispondendo qualcosa al proprietario e incassando gli ancora ricchi sussidi (con rendimenti dell\'8-10 per cento l\'anno per vent\'anni sul capitale investito, meglio dei BTp). Si tratta di </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>uno dei trend di sviluppo</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> delle rinnovabili nel nostro Paese che è protagonista dell\'annuale </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>”Rapporto sulle nuove energie in Italia”</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, giunto alla terza edizione, che verrà presentato nel corso di Microgen Forum a Milano il prossimo 21 marzo. </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Il fenomeno della </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>caccia ai tetti</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, presente in altri Paesi ma finora marginale da noi, ha iniziato a manifestarsi con forza negli ultimi mesi del 2011 a seguito di due condizioni. Da una parte, l\'entrata in vigore del </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>Quarto Conto Energia</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, che di fatto continua a garantire sussidi generosi solo agli impianti su edifici inferiori al MW di potenza; dall\'altra, il </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>credit </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>crunch</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, che ha reso molto difficile l\'accesso ai finanziamenti in prima persona da parte dei </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">proprietari dei tetti, di solito aziende agricole o singoli agricoltori, anche per progetti di fatto garantiti.</span></span></span>\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">La finestra resterà aperta per </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>un tempo limitato</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">: già dal gennaio 2013 i sussidi perderanno la componente acquisto dell\'energia, che oggi rende per circa 10 centesimi a KWh, dando un taglio deciso ai rendimenti. Ed ecco i cacciatori di tetti in giro per la campagne di tutta Italia, con privilegio per le aree con numerosi allevamenti zootecnici, che sono le aziende con </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>i capannoni più grandi</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">. Armati di brochure e macchine fotografiche digitali, con competenze di impianti fotovoltaici ma anche di strutture edilizie e coperture, si muovono per lo più guidati dal passaparola da un agricoltore all\'altro. Sono aiutati anche dagli incentivi (crediti d\'imposta) legati alla rimozione e </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>smaltimento delle </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>coperture contenenti amianto</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">, e possono proporre di prendere due piccioni con una </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">fava.\n</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>I “mandanti” </b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">dei cacciatori d tetti sono dei tipi più diversi. Ci sono le tradizionali utilities,</span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">come Sorgenia, appartenente a un gruppo pioniere nel campo dei comodati dei tetti. Ma ci sono anche aziende più specializzate, dei broker che si occupano delle pratiche burocratiche e di mettere insieme proprietari di tetti, impiantisti, smaltitori di rifiuti speciali e soprattutto </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>finanziatori</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">. Sono questi ultimi infatti i veri “dei ex machina” del business dei tetti. Fondi d\'investimento, coordinamenti di facoltosi privati, banche, tutti soggetti con a disposizione grossi capitali, che ragionano in termini di ritorno dell\'investimento.</span></span></span></p>\n<p align=\"JUSTIFY\"><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\">Per costoro le figure dei broker indipendenti cacciatori di tetti sono indispensabili, anche perchè consentono una </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>flessibilità</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> di dimensionamento degli impianti impossibile in altro modo. Per fare un esempio: diventa possibile </span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"><b>aggirare il vincolo del MW</b></span></span></span><span style=\"color: #535353;\"><span style=\"font-family: Arial, serif;\"><span style=\"font-size: medium;\"> su una certa superficie previsto dal Quarto Conto Energia. Un singolo investitore non potrebbe per esempio sfruttare i tetti di un grosso allevamento con una superficie utile di 1,8 MW. Ma con un broker si può, perch